import React, { useEffect, useState } from 'react'
import { TopBanner, Home } from './views'
import { Navbar } from './components/nav'
import './App.css'

const App = () => {
  const [isMobile, setMobile] = useState(false)
  const [currentWidth, setWidth] = useState(0)

  const isMobileBool = () => window.innerWidth < 440

  useEffect(() => {
    setWidth(window.innerWidth)
    if (isMobileBool() !== isMobile) {
      setMobile(isMobileBool())
    }

    window.onresize = () => {
      setWidth(window.innerWidth)
      if (isMobileBool() !== isMobile) {
        setMobile(isMobileBool())
      }
    }
  })

  return (
    <>
      {isMobile ? (
        <TopBanner mainHeader='50px' />
      ) : (
        <TopBanner mainHeader='60px' />
      )}
      {isMobile ? (
        <Navbar titleSize='35px' magicSize='13px' />
      ) : (
        <Navbar titleSize='40px' magicSize='15px' />
      )}
      <div
        className='container-fluid'
        style={
          {
            // backgroundColor: 'white',
            // height: '700px',
            // padding: '20px 0 20px 0',
          }
        }
      >
        <div className='row'>
          <div
            className='col-md-4'
            style={{
              height: '100%',
            }}
          ></div>
          <div
            className='col-md-4'
            style={{
              overflow: 'hidden',
              backgroundColor: '#ecf0f1',
              boxShadow:
                // '0px 0px 25px 0px red, 0px 0px 25px 0px red',

                '-60px 0px 100px -90px #919191, 60px 0px 100px -90px #919191',
            }}
          >
            <div
              style={{
                minHeight: '330px',
              }}
            ></div>
          </div>
          <div
            className='col-md-4'
            style={{
              height: '100%',
            }}
          ></div>
          {/* <Home currentWidth={currentWidth} isMobile={isMobile} /> */}
        </div>
        <div className='row'>
          <div
            className='col-12'
            style={{
              textAlign: 'center',
              display: 'flex',
              justifyContent: 'center',
              alignContent: 'center',
            }}
          >
            <div
              style={{
                margin: '10px 20px',
              }}
            >
              <p
                style={{
                  backgroundColor: '#348c94',
                  padding: '7px 10px',
                  borderRadius: '7px',
                  fontSize: '22px',
                  color: 'white',
                  width: '200px',
                  fontWeight: 'bolder',
                }}
              >
                Click Here!
              </p>
            </div>
            <div
              style={{
                margin: '10px 20px',
              }}
            >
              <p
                style={{
                  backgroundColor: '#374041',
                  padding: '7px 0',
                  borderRadius: '7px',
                  fontSize: '22px',
                  color: 'white',
                  width: '200px',
                  fontWeight: 'bolder',
                }}
              >
                Here Too!
              </p>
            </div>
          </div>
        </div>
        {/* <div
          className='row'
          style={{
            height: '2px',
            backgroundColor: '#b4b7b7',
          }}
        ></div> */}
        <div
          className='row'
          style={{
            height: '10px',
            backgroundColor: '#e6e7e7',
          }}
        >
          <div
            className='col-12'
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              padding: '0',
            }}
          >
            <div
              style={{
                width: '20%',
                height: '100%',
                backgroundColor: '#42ba88',
              }}
            ></div>
            <div
              style={{
                width: '20%',
                height: '100%',
                backgroundColor: '#4274ba',
              }}
            ></div>
            <div
              style={{
                width: '20%',
                height: '100%',
                backgroundColor: '#42B0BA',
              }}
            ></div>
            <div
              style={{
                width: '20%',
                height: '100%',
                backgroundColor: '#ba4c42',
              }}
            ></div>
            <div
              style={{
                width: '20%',
                height: '100%',
                backgroundColor: '#dbbaaf',
              }}
            ></div>
          </div>
        </div>
        <div
          className='row'
          style={{
            height: '1000px',
          }}
        ></div>
      </div>
    </>
  )
}

export default App
