import React, { useEffect, useState } from 'react'
import { TopBanner, Home } from './views'
import { Navbar } from './components/nav'
import './App.css'

const App = () => {
  const [isMobile, setMobile] = useState(false)
  const [currentWidth, setWidth] = useState(0)

  const isMobileBool = () => window.innerWidth < 440

  useEffect(() => {
    setWidth(window.innerWidth)
    if (isMobileBool() !== isMobile) {
      setMobile(isMobileBool())
    }

    window.onresize = () => {
      setWidth(window.innerWidth)
      if (isMobileBool() !== isMobile) {
        setMobile(isMobileBool())
      }
    }
  })

  return (
    <>
      {isMobile ? (
        <TopBanner mainHeader='50px' />
      ) : (
        <TopBanner mainHeader='60px' />
      )}
      {isMobile ? (
        <Navbar titleSize='35px' magicSize='13px' />
      ) : (
        <Navbar titleSize='40px' magicSize='15px' />
      )}
      <div
        className='container-fluid'
        style={{
          backgroundColor: 'white',
          // height: '700px',
          // padding: '20px 0 20px 0',
        }}
      >
        <div className='row'>
          <div
            className='col-md-4'
            style={{
              height: '100%',
            }}
          ></div>
          <div
            className='col-md-4'
            style={{
              overflow: 'hidden',
              boxShadow:
                // '0px 0px 25px 0px red, 0px 0px 25px 0px red',

                '-60px 0px 100px -90px #919191, 60px 0px 100px -90px #919191',
            }}
          >
            <div
              style={{
                minHeight: '400px',
              }}
            ></div>
          </div>
          <div
            className='col-md-4'
            style={{
              height: '100%',
            }}
          ></div>
          {/* <Home currentWidth={currentWidth} isMobile={isMobile} /> */}
        </div>
        <div className='row'>
          <div
            className='col-12'
            style={{
              textAlign: 'center',
              display: 'flex',
              justifyContent: 'center',
              alignContent: 'center',
            }}
          >
            <p
              style={{
                backgroundColor: '#348c94',
                margin: '10px',
                padding: '5px 10px',
                borderRadius: '7px',
                fontSize: '20px',
                color: 'white',
              }}
            >
              Click Here!
            </p>
            <p
              style={{
                backgroundColor: '#374041',
                margin: '10px',
                padding: '5px 10px',
                borderRadius: '7px',
                fontSize: '20px',
                color: 'white',
              }}
            >
              Here Too!
            </p>
          </div>
        </div>
      </div>
    </>
  )
}

export default App
