import React, { useEffect, useState } from 'react'
import { TopBanner, Home } from './views'
import { Navbar } from './components/nav'
import './App.css'

const App = () => {
  const [isMobile, setMobile] = useState(false)
  const [currentWidth, setWidth] = useState(0)

  const isMobileBool = () => window.innerWidth < 440

  useEffect(() => {
    setWidth(window.innerWidth)
    if (isMobileBool() !== isMobile) {
      setMobile(isMobileBool())
    }

    window.onresize = () => {
      setWidth(window.innerWidth)
      if (isMobileBool() !== isMobile) {
        setMobile(isMobileBool())
      }
    }
  })

  return (
    <>
      {isMobile ? (
        <TopBanner mainHeader='50px' />
      ) : (
        <TopBanner mainHeader='60px' />
      )}
      {isMobile ? (
        <Navbar titleSize='35px' magicSize='13px' />
      ) : (
        <Navbar titleSize='40px' magicSize='15px' />
      )}
      <div
        className='container-fluid'
        style={{
          backgroundColor: 'white',
          // height: '700px',
          // padding: '20px 0 20px 0',
        }}
      >
        <div>
          <div
            style={{
              // overflow: 'hidden',
              height: '400px',
            }}
          >
            <div
              style={{
                width: '33.3%',
                height: '100%',
                zIndex: '1',
                margin: '0 auto',
                display: 'inline-block',
              }}
            ></div>
            <div
              style={{
                backgroundColor: '#fdfdfd',
                width: '23.3%',
                height: '100%',
                margin: '0 auto',
                display: 'inline-block',
                boxShadow:
                  '-60px 0px 100px -90px #a9a9a9, 60px 0px 100px -90px #a9a9a9',
              }}
            >
              {/* Hello World */}
              <div>
                {/* <span
                  style={{
                    boxShadow:
                      '-60px 0px 100px -90px #a9a9a9, 60px 0px 100px -90px #a9a9a9',
                  }}
                >
                  Hello World
                </span> */}
              </div>
            </div>
            <div
              style={{
                width: '33.3%',
                zIndex: '1',
                height: '100%',
                margin: '0 auto',
                display: 'inline-block',
              }}
            ></div>
          </div>
          {/* <Home currentWidth={currentWidth} isMobile={isMobile} /> */}
        </div>
      </div>
    </>
  )
}

export default App
